	my $class = $args{for_class}
		or Carp::confess("Cannot call init_meta without specifying a for_class");

	my $metaclass  = $args{metaclass}  || 'TB2::Mouse::Meta::Role';

	my $meta = $metaclass->initialize($class);

	$meta->add_method(meta => sub{
